MyCForm[a_] := Module[{res, res1},
   res = ToCCodeString[CExpression[a]];
   (*Print["In ",res, " ",a];*)
   res1 = 
    StringReplace[res, 
     RegularExpression["Complex\\(0.,([0-9\\.]+)\\)"] :> 
      " (+ I*($1))"];
   res = StringReplace[res1, 
     RegularExpression["Complex\\(([0-9\\.]+),([0-9\\.]+)\\)"] :> 
      " ($1 + I*$2)"];
   res1 = 
    StringReplace[res, 
     RegularExpression["Complex\\(([0-9]+),([0-9]+)\\)"] :> 
      " ($1 + I*$2)"];
   res = StringReplace[res1, 
     RegularExpression["Complex\\(([0-9\\.]+),-([0-9\\.]+)\\)"] :> 
      " ($1 - I*$2)"];
   res1 = 
    StringReplace[res, 
     RegularExpression["Complex\\(([0-9]+),-([0-9\\.]+)\\)"] :> 
      " ($1 - I*$2)"];
     (*Print["Out ",res1];*)
   res1];

